-- Create the table to store words and their counts\nCREATE TABLE word_counts (\n    word VARCHAR(255) NOT NULL,\n    count INT DEFAULT 1,\n    PRIMARY KEY(word)\n);\n\n-- Insert words into the word_counts table\nINSERT INTO word_counts (word)\nSELECT LOWER(word) FROM (\n    SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(t.blob_column, ' ', numbers.n), ' ', -1) word\n    FROM (SELECT 1 n UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 \n          UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10) numbers\n    INNER JOIN your_table t ON CHAR_LENGTH(t.blob_column)\n       -CHAR_LENGTH(REPLACE(t.blob_column, ' ', ''))>=numbers.n-1\n) words;\n\n-- Update the counts of each word\nUPDATE word_counts wc\nJOIN (\n    SELECT LOWER(word) word, COUNT(*) count\n    FROM (\n        SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(t.blob_column, ' ', numbers.n), ' ', -1) word\n        FROM (SELECT 1 n UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 \n              UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10) numbers\n        INNER JOIN your_table t ON CHAR_LENGTH(t.blob_column)\n           -CHAR_LENGTH(REPLACE(t.blob_column, ' ', ''))>=numbers.n-1\n    ) words\n    GROUP BY word\n) wc_new ON wc.word = wc_new.word\nSET wc.count = wc_new.count;\n\n-- Select the most popular words\nSELECT word FROM word_counts ORDER BY count DESC LIMIT 10;