WITH DateSeries AS (\n    SELECT \n        CAST('2023-01-01' AS DATE) AS DateValue\n    UNION ALL\n    SELECT \n        DATEADD(DAY, 1, DateValue)\n    FROM \n        DateSeries\n    WHERE \n        DateValue < '2023-12-31'\n)\nSELECT \n    ds.DateValue,\n    COUNT(t.YourDateColumn) AS RecordCount\nFROM \n    DateSeries ds\nLEFT JOIN \n    YourTable t\nON \n    ds.DateValue = t.YourDateColumn\nGROUP BY \n    ds.DateValue\nORDER BY \n    ds.DateValue\nOPTION (MAXRECURSION 0); -- Allows the recursion to go beyond the default 100 levels