-- Create a table and insert data\nCREATE TABLE my_table (id NUMBER, name VARCHAR2(50));\nINSERT INTO my_table VALUES (1, 'Alice');\nINSERT INTO my_table VALUES (2, 'Bob');\nCOMMIT;\n\n-- Check ROWID before reorganization\nSELECT ROWID, id, name FROM my_table;\n\n-- Perform a table reorganization\nALTER TABLE my_table MOVE;\n\n-- Check ROWID after reorganization\nSELECT ROWID, id, name FROM my_table;