SELECT \n    p.Name AS PlayerName,\n    c.ClubName,\n    r.Start_date,\n    r.End_date\nFROM\n    Registrations r\n    INNER JOIN Players p ON r.PlayerID = p.ID\n    INNER JOIN Teams t ON r.TeamID = t.ID\n    INNER JOIN Clubs c ON t.ClubID = c.ID\nWHERE\n    r.SeasonID = '2008'\n    AND p.ID IN (\n        SELECT \n            r.PlayerID\n        FROM \n            Registrations r\n            INNER JOIN Teams t ON r.TeamID = t.ID\n        GROUP BY \n            r.PlayerID, r.SeasonID\n        HAVING \n            COUNT(DISTINCT t.ClubID) > 1\n    )\nORDER BY \n    p.Name, r.Start_date;