CREATE PROCEDURE CopyNewSub1Records (IN oldID INT, IN newID INT)\nBEGIN\n    DECLARE done INT DEFAULT 0;\n    DECLARE sub1_id INT;\n    DECLARE sub1_column1, sub1_column2, ... VARCHAR(255);  -- Adjust types as needed\n\n    DECLARE cur CURSOR FOR\n        SELECT column1, column2, ...\n        FROM SUB1\n        WHERE FK_ID = oldID;\n\n    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;\n\n    OPEN cur;\n\n    read_loop: LOOP\n        FETCH cur INTO sub1_column1, sub1_column2, ...;\n        IF done THEN\n            LEAVE read_loop;\n        END IF;\n\n        -- Insert into SUB1 with newID\n        INSERT INTO SUB1 (FK_ID, column1, column2, ...)\n        VALUES (newID, sub1_column1, sub1_column2, ...);\n    END LOOP;\n\n    CLOSE cur;\nEND;