-- Creating a table with a BINARY(16) column for UUID\nCREATE TABLE example_table (\n    id BINARY(16) PRIMARY KEY,\n    name VARCHAR(255) NOT NULL\n);\n\n-- Inserting a record with a UUID\nINSERT INTO example_table (id, name) VALUES (UNHEX(REPLACE(UUID(), '-', '')), 'Example Name');\n\n-- Selecting and converting the binary UUID back to string format\nSELECT HEX(id) AS uuid, name FROM example_table;