-- Step 1: Create a new temporary table with an additional character column\nCREATE TEMP TABLE new_temp_table (\n    numeric_column INTEGER,\n    char_column CHAR(2)\n);\n\n-- Step 2: Insert data into the new temporary table with the necessary transformations\nINSERT INTO new_temp_table (numeric_column, char_column)\nSELECT numeric_column,\n       CASE \n           WHEN numeric_column <= 1 THEN 'SP'\n           ELSE 'FA'\n       END\nFROM original_temp_table;\n\n-- Now you can use new_temp_table for further processing while preserving the original data.