CREATE OR REPLACE FUNCTION extract_digits(input_str IN VARCHAR2)\n    RETURN VARCHAR2\nIS\n    result VARCHAR2(100) := '';\n    current_char CHAR(1);\nBEGIN\n    FOR i IN 1 .. LENGTH(input_str) LOOP\n        current_char := SUBSTR(input_str, i, 1);\n        \n        -- Check if the character is a digit\n        IF current_char BETWEEN '0' AND '9' THEN\n            result := result || current_char;\n        END IF;\n    END LOOP;\n    \n    RETURN result;\nEND;\n/