WITH CTE_Union AS (\n  SELECT\n    Column1 AS c1,\n    ...,\n    ColumnN AS cN,\n    'database1' AS dbName\n  FROM\n    database1.dbo.Table1\n\n  UNION ALL\n\n  SELECT\n    'Some String' AS c1,\n    ...,\n    NULL AS cN,\n    'database1' AS dbName\n  FROM\n    database1.dbo.Table2\n\n  UNION ALL\n\n  SELECT\n    Column1 AS c1,\n    ...,\n    ColumnN AS cN,\n    'database2' AS dbName\n  FROM\n    database2.dbo.Table1\n\n  UNION ALL\n\n  SELECT\n    'Some String' AS c1,\n    ...,\n    NULL AS cN,\n    'database2' AS dbName\n  FROM\n    database2.dbo.Table2\n)\n\nSELECT DISTINCT\n  c1,\n  ...,\n  cN\nFROM\n  CTE_Union;