SELECT \n    s.name AS 'SchemaName',\n    q.name AS 'QueueName',\n    p.name AS 'ProcedureName',\n    ss.user_name AS 'ExecutionUser'\nFROM \n    sys.service_queues q\nJOIN \n    sys.services s ON q.service_id = s.service_id\nJOIN \n    sys.procedures p ON q.object_id = p.object_id\nJOIN \n    sys.service_contract_usages scu ON s.service_id = scu.service_id\nJOIN \n    sys.service_contracts sc ON scu.contract_id = sc.contract_id\nJOIN \n    sys.service_contract_message_usages scmu ON sc.contract_id = scmu.contract_id\nJOIN \n    sys.server_principals ss ON ss.principal_id = p.principal_id\nWHERE \n    p.name = 'FileUploadAsyncWorker';