-- Create a table with a CLOB column\nCREATE TABLE my_table (\n    id NUMBER PRIMARY KEY,\n    data CLOB\n);\n\n-- PL/SQL block to insert Base64 encoded data\nDECLARE\n    my_data CLOB;\n    base64_encoded_data CLOB := 'your_base64_encoded_data_here';\nBEGIN\n    INSERT INTO my_table (id, data) VALUES (1, base64_encoded_data);\nEND;\n/\n\n-- PL/SQL block to retrieve and decode Base64 data\nDECLARE\n    encoded_data CLOB;\n    decoded_data BLOB;\nBEGIN\n    SELECT data INTO encoded_data FROM my_table WHERE id = 1;\n\n    -- Decode the Base64 encoded data (this example assumes you have a function to decode Base64)\n    decoded_data := base64_decode(encoded_data);\n\n    -- Use the decoded data as needed\nEND;\n/