BEGIN\n     CTX_DDL.CREATE_PREFERENCE('my_datastore', 'MULTI_COLUMN_DATASTORE');\n     CTX_DDL.SET_ATTRIBUTE('my_datastore', 'COLUMNS', 'first_name, last_name, address, property_details');\n\n     EXECUTE IMMEDIATE 'CREATE INDEX property_owners_idx ON property_owners_view(combined_text) \n                        INDEXTYPE IS CTXSYS.CONTEXT \n                        PARAMETERS(''DATASTORE my_datastore'')';\n   END;\n   /