DECLARE @CustomerID INT;\nDECLARE @CustomerName VARCHAR(100);\n\nDECLARE cur CURSOR READ_ONLY FOR\nSELECT CustomerID, CustomerName\nFROM Customers;\n\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @CustomerID, @CustomerName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    BEGIN TRY\n        EXEC AddCustomer @CustomerID, @CustomerName;\n    END TRY\n    BEGIN CATCH\n        -- Log the error details\n        PRINT 'Error occurred for CustomerID: ' + CAST(@CustomerID AS VARCHAR) + ', CustomerName: ' + @CustomerName;\n    END CATCH\n    \n    FETCH NEXT FROM cur INTO @CustomerID, @CustomerName;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;