-- Function to get Invoice by ID\nCREATE FUNCTION dbo.GetInvoiceByID(@id INT)\nRETURNS TABLE\nAS\nRETURN\n(\n    SELECT * FROM Invoice WHERE InvoiceID = @id\n);\n\n-- Function to get Invoice Items by Invoice ID\nCREATE FUNCTION dbo.GetInvoiceItemsByInvoiceID(@id INT)\nRETURNS TABLE\nAS\nRETURN\n(\n    SELECT * FROM InvoiceItem WHERE InvoiceID = @id\n);\n\n-- Function to get Invoice Comments by Invoice ID\nCREATE FUNCTION dbo.GetInvoiceCommentsByInvoiceID(@id INT)\nRETURNS TABLE\nAS\nRETURN\n(\n    SELECT * FROM InvoiceComments WHERE InvoiceID = @id\n);