-- Procedure to select active invoices\nCREATE PROCEDURE hd_invoice_selectActive() AS\nBEGIN\n    SELECT * FROM Invoice WHERE Status = 'Active';\n    SELECT * FROM InvoiceItem WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE Status = 'Active');\n    SELECT * FROM InvoiceComments WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE Status = 'Active');\nEND;\n\n-- Procedure to select overdue invoices\nCREATE PROCEDURE hd_invoice_selectOverdue() AS\nBEGIN\n    SELECT * FROM Invoice WHERE DueDate < GETDATE();\n    SELECT * FROM InvoiceItem WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE DueDate < GETDATE());\n    SELECT * FROM InvoiceComments WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE DueDate < GETDATE());\nEND;\n\n-- Procedure to select invoices for a specific month\nCREATE PROCEDURE hd_invoice_selectForMonth(@year INT, @month INT) AS\nBEGIN\n    SELECT * FROM Invoice WHERE YEAR(InvoiceDate) = @year AND MONTH(InvoiceDate) = @month;\n    SELECT * FROM InvoiceItem WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE YEAR(InvoiceDate) = @year AND MONTH(InvoiceDate) = @month);\n    SELECT * FROM InvoiceComments WHERE InvoiceID IN \n        (SELECT InvoiceID FROM Invoice WHERE YEAR(InvoiceDate) = @year AND MONTH(InvoiceDate) = @month);\nEND;