SELECT \n    p.PatientID, \n    v.VisitDate AS FirstVisitDate, \n    v.HeartRate AS FirstVisitHeartRate\nFROM \n    Patient p\nINNER JOIN \n    Visit v ON p.PatientID = v.PatientID\nINNER JOIN \n    (SELECT \n         PatientID, \n         MIN(VisitDate) AS FirstVisitDate\n     FROM \n         Visit\n     GROUP BY \n         PatientID) fv ON v.PatientID = fv.PatientID AND v.VisitDate = fv.FirstVisitDate