CREATE FUNCTION dbo.SplitString\n(\n    @Input NVARCHAR(MAX),\n    @Delimiter CHAR(1)\n)\nRETURNS @Output TABLE (Item BIGINT)\nAS\nBEGIN\n    DECLARE @Start INT, @End INT\n    SET @Start = 1\n    WHILE CHARINDEX(@Delimiter, @Input, @Start) > 0\n    BEGIN\n        SET @End = CHARINDEX(@Delimiter, @Input, @Start)\n        INSERT INTO @Output (Item)\n        VALUES (CAST(SUBSTRING(@Input, @Start, @End - @Start) AS BIGINT))\n        SET @Start = @End + 1\n    END\n    INSERT INTO @Output (Item)\n    VALUES (CAST(SUBSTRING(@Input, @Start, LEN(@Input) - @Start + 1) AS BIGINT))\n    RETURN\nEND