-- Add the redundant column\nALTER TABLE Projects ADD OfficeID INT;\n\n-- Update the OfficeID in Projects initially\nUPDATE Projects P\nJOIN Employees E   ON P.CreatedByID = E.EmpID\nJOIN Departments D ON E.depID = D.depID\nSET P.OfficeID = D.officeID;\n\n-- Add a trigger to maintain the OfficeID consistency\nCREATE TRIGGER trg_update_officeid\nAFTER UPDATE ON Employees\nFOR EACH ROW\nBEGIN\n    UPDATE Projects\n    SET OfficeID = (SELECT D.officeID FROM Departments D WHERE D.depID = NEW.depID)\n    WHERE CreatedByID = NEW.empID;\nEND;\n\n-- Adjusted query\nSELECT * \nFROM Projects P\nWHERE P.OfficeID = @SomeOfficeID;