-- Ensure these indexes exist\nCREATE INDEX idx_projects_createdby ON Projects(CreatedByID);\nCREATE INDEX idx_employees_depid ON Employees(depID);\nCREATE INDEX idx_departments_officeid ON Departments(officeID);\n\n-- Your query\nSELECT * \nFROM Projects P\nJOIN Employees E   ON P.CreatedBY = E.EmpID\nJOIN Departments D ON E.DepID = D.DepID\nJOIN Offices O     ON D.officeID = O.officeID\nWHERE O.officeID = @SomeOfficeID;