-- Example of a T-SQL stored procedure that calls a managed SP and handles exceptions\nCREATE PROCEDURE MyTsqlProcedure\nAS\nBEGIN\n    BEGIN TRY\n        -- Call the managed stored procedure\n        EXEC dbo.MyManagedProcedure;\n    END TRY\n    BEGIN CATCH\n        -- Handle the exception\n        DECLARE @ErrorMessage NVARCHAR(4000);\n        DECLARE @ErrorSeverity INT;\n        DECLARE @ErrorState INT;\n\n        SET @ErrorMessage = ERROR_MESSAGE();\n        SET @ErrorSeverity = ERROR_SEVERITY();\n        SET @ErrorState = ERROR_STATE();\n\n        -- Log the error or take any necessary action\n        PRINT 'An error occurred: ' + @ErrorMessage;\n\n        -- Optionally, you can re-throw the error if you want to propagate it\n        -- RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState);\n    END CATCH\nEND;\nGO