-- Example of creating a queue\n   BEGIN\n      DBMS_AQADM.CREATE_QUEUE_TABLE(\n         queue_table        => 'my_queue_table',\n         queue_payload_type => 'SYS.ANYDATA'\n      );\n   \n      DBMS_AQADM.CREATE_QUEUE(\n         queue_name => 'my_queue',\n         queue_table => 'my_queue_table'\n      );\n\n      DBMS_AQADM.START_QUEUE(\n         queue_name => 'my_queue'\n      );\n   END;