CREATE TABLE Vessel (\n    VesselID INT PRIMARY KEY,\n    -- other vessel fields\n);\n\nCREATE TABLE FuelConsumption (\n    ConsumptionID INT PRIMARY KEY,\n    VesselID INT,\n    LocationType NVARCHAR(10) CHECK (LocationType IN ('AtPort', 'Sailing')),\n    FuelType NVARCHAR(10) CHECK (FuelType IN ('IFO180', 'IFO380', 'MDO', 'MGO')),\n    ConsumptionValue INT,\n    FOREIGN KEY (VesselID) REFERENCES Vessel(VesselID)\n);