SELECT \n    dp.name AS Database_Principal,\n    o.name AS Object_Name\nFROM \n    sys.database_permissions AS p\nJOIN \n    sys.objects AS o ON p.major_id = o.object_id\nJOIN \n    sys.database_principals AS dp ON p.grantee_principal_id = dp.principal_id\nWHERE \n    o.type = 'U'  -- 'U' stands for user tables\n    AND p.permission_name = 'DELETE'\n    AND dp.name = 'YourUserName'\nORDER BY \n    o.name;