BEGIN TRANSACTION;\n\n-- Create a temporary table to hold the first row\nSELECT * INTO #TempRow1 FROM YourTable WHERE ID = 1;\n\n-- Create a temporary table to hold the second row\nSELECT * INTO #TempRow2 FROM YourTable WHERE ID = 2;\n\n-- Swap the rows\nUPDATE YourTable\nSET CODE = (SELECT CODE FROM #TempRow2),\n    NAME = (SELECT NAME FROM #TempRow2)\nWHERE ID = 1;\n\nUPDATE YourTable\nSET CODE = (SELECT CODE FROM #TempRow1),\n    NAME = (SELECT NAME FROM #TempRow1)\nWHERE ID = 2;\n\n-- Clean up temporary tables\nDROP TABLE #TempRow1;\nDROP TABLE #TempRow2;\n\nCOMMIT TRANSACTION;