SELECT entities.id, \n       entities.name, \n       scores.score, \n       scores.date_added \nFROM entities\nLEFT JOIN (\n    SELECT entity_id, \n           score, \n           date_added \n    FROM scores \n    WHERE (entity_id, date_added) IN (\n        SELECT entity_id, MAX(date_added) \n        FROM scores \n        GROUP BY entity_id\n    )\n) AS scores \nON entities.id = scores.entity_id;