DECLARE\n       avg_row_size NUMBER;\n       future_rows NUMBER := 12000; -- Example future row count\n       future_table_size NUMBER;\n   BEGIN\n       SELECT AVG(VSIZE(column1) + VSIZE(column2) + ... + VSIZE(columnN))\n       INTO avg_row_size\n       FROM your_table;\n       \n       future_table_size := future_rows * avg_row_size;\n       DBMS_OUTPUT.PUT_LINE('Future table size (bytes): ' || future_table_size);\n   END;\n   /