-- Pre-deployment script\nBEGIN TRY\n    -- Switch to master for necessary operations\n    USE master;\n    \n    -- Your operations in master database\n    -- ...\n    \n    -- Switch back to the target database\n    USE [YourTargetDatabase];\n    \n    -- Check if the schema exists before creating it\n    IF NOT EXISTS (SELECT * FROM sys.schemas WHERE name = 'DataWarehouseReports')\n    BEGIN\n        CREATE SCHEMA DataWarehouseReports;\n    END\nEND TRY\nBEGIN CATCH\n    -- Handle errors\n    PRINT 'An error occurred: ' + ERROR_MESSAGE();\n    -- Optionally, you can log the error or take other actions\nEND CATCH