select * from table t \nwhere exists (\n    select 1 from othertable o \n    where t.nameid = o.nameid and otherdesc = 'SomeDesc'\n);\n\n-- This alias 'o' is scoped within this subquery\nselect * from table t \nwhere exists (\n    select 1 from othertable o \n    where t.nameid = o.nameid and otherdesc = 'SomeDesc'\n)\nand exists (\n    select 1 from othertable o \n    where t.nameid = o.nameid and otherdesc = 'SomeOtherDesc'\n);\n\n-- Both subqueries have their own independent scope