DECLARE @order INT = 100; -- The specific ORDER value you are looking for\nDECLARE @RID INT;\nDECLARE @School NVARCHAR(100);\nDECLARE @Order INT;\nDECLARE @Text NVARCHAR(MAX);\n\nDECLARE cursor_school_data CURSOR FOR\nSELECT RID, School, [Order], [Text]\nFROM SCHOOL_DATA\nWHERE School = 'YourSchoolName'\nORDER BY [Order];\n\nOPEN cursor_school_data;\n\nFETCH NEXT FROM cursor_school_data INTO @RID, @School, @Order, @Text;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    IF @Order = @order\n    BEGIN\n        -- You found the row with ORDER = @order\n        PRINT 'Found the row:';\n        PRINT @RID;\n        PRINT @School;\n        PRINT @Order;\n        PRINT @Text;\n        -- Perform any other operations you need here\n        BREAK;\n    END\n\n    FETCH NEXT FROM cursor_school_data INTO @RID, @School, @Order, @Text;\nEND\n\nCLOSE cursor_school_data;\nDEALLOCATE cursor_school_data;