-- Create a new role for read-only access\nCREATE ROLE db_readonly;\n\n-- Grant SELECT permission on tables and views\nGRANT SELECT ON SCHEMA::dbo TO db_readonly;\n\n-- Grant EXECUTE permission on stored procedures\nGRANT EXECUTE ON SCHEMA::dbo TO db_readonly;\n\n-- Grant VIEW DEFINITION permission to see the schema and SP code\nGRANT VIEW DEFINITION ON SCHEMA::dbo TO db_readonly;\n\n-- Add the users to the new role\nALTER ROLE db_readonly ADD MEMBER [User1];\nALTER ROLE db_readonly ADD MEMBER [User2];