UPDATE volunteers v\nJOIN mailing_list m ON v.id = m.id\nSET \n    v.column1 = CASE \n                    WHEN @table_name = 'volunteers' THEN @new_value_for_column1 \n                    ELSE v.column1 \n                END,\n    v.column2 = CASE \n                    WHEN @table_name = 'volunteers' THEN @new_value_for_column2 \n                    ELSE v.column2 \n                END,\n    m.column3 = CASE \n                    WHEN @table_name = 'mailing_list' THEN @new_value_for_column3 \n                    ELSE m.column3 \n                END,\n    m.column4 = CASE \n                    WHEN @table_name = 'mailing_list' THEN @new_value_for_column4 \n                    ELSE m.column4 \n                END\nWHERE v.id = @id;