DECLARE\n  V_TEMP_TABTYPE MY_TABTYPE;\nBEGIN\n  -- Populate the collection with some sample data\n  V_TEMP_TABTYPE := MY_TABTYPE(\n    MY_TYPE('First', 1),\n    MY_TYPE('Second', 2),\n    MY_TYPE('Third', 3)\n  );\n\n  -- Iterate over the collection and print each element\n  FOR i IN 1..V_TEMP_TABTYPE.COUNT LOOP\n    dbms_output.put_line('MyString: ' || V_TEMP_TABTYPE(i).MyString || ', Counter: ' || V_TEMP_TABTYPE(i).counter);\n  END LOOP;\nEND;\n/