CREATE TRIGGER trgEncryptCreditCard\n    ON YourTable\n    INSTEAD OF INSERT\n    AS\n    BEGIN\n        DECLARE @encryptedCreditCardNumber varbinary(max)\n        DECLARE @encodedCreditCardNumber varchar(max)\n        \n        INSERT INTO YourTable (creditcard, other_columns)\n        SELECT \n            dbo.fn_varbinaryToBase64(EncryptByKey(Key_GUID('testKey'), inserted.creditcard)),\n            inserted.other_columns\n        FROM inserted\n    END