CREATE TRIGGER trg_check_part_number\nON import_table\nFOR INSERT, UPDATE\nAS\nBEGIN\n    IF EXISTS (\n        SELECT 1\n        FROM inserted i\n        LEFT JOIN inventory_master im\n        ON i.part_number = im.part_number\n        AND im.type = 'C'\n        WHERE im.part_number IS NULL\n    )\n    BEGIN\n        RAISERROR ('The part_number must exist in inventory_master with type ''C''.', 16, 1)\n        ROLLBACK TRANSACTION\n    END\nEND