DECLARE @TableName NVARCHAR(256)\nSET @TableName = 'YourTableName' -- Replace with your table name\n\nSELECT \n    o.name AS ProcedureName,\n    m.definition AS ProcedureDefinition\nFROM \n    sys.sql_modules m\nINNER JOIN \n    sys.objects o ON m.object_id = o.object_id\nWHERE \n    m.definition LIKE '%' + @TableName + '%'\n    AND o.type = 'P'\n    AND (m.definition LIKE '%INSERT%' OR m.definition LIKE '%UPDATE%')\nORDER BY \n    o.name;