SELECT\n    category.id,\n    category.title,\n    COUNT(CASE WHEN ts.section_id = 1 THEN 1 END) AS doc1,\n    COUNT(CASE WHEN ts.section_id = 2 THEN 1 END) AS doc2,\n    COUNT(CASE WHEN ts.section_id = 3 THEN 1 END) AS doc3,\n    COUNT(CASE WHEN ts.section_id = 4 THEN 1 END) AS doc4\nFROM\n    category \n    LEFT JOIN category_link_section AS ts\n        ON category.id = ts.category_id\nGROUP BY\n    category.id,\n    category.title;