DECLARE\n     l_file UTL_FILE.FILE_TYPE;\n     l_directory VARCHAR2(255) := 'YOUR_DIRECTORY';\n     l_filename VARCHAR2(255) := 'email_dump.txt';\n     l_email_content VARCHAR2(32767);\n   BEGIN\n     l_email_content := 'Subject: Test Mail' || CHR(13) || CHR(10) ||\n                        'This is a test mail.';\n                        \n     l_file := UTL_FILE.FOPEN(l_directory, l_filename, 'W');\n     UTL_FILE.PUT_LINE(l_file, l_email_content);\n     UTL_FILE.FCLOSE(l_file);\n   EXCEPTION\n     WHEN OTHERS THEN\n       IF UTL_FILE.IS_OPEN(l_file) THEN\n         UTL_FILE.FCLOSE(l_file);\n       END IF;\n       RAISE;\n   END;