DECLARE\n       CURSOR emp_cursor IS SELECT last_name FROM employees WHERE department_id = 10;\n       v_employee_name employees.last_name%TYPE;\n     BEGIN\n       OPEN emp_cursor;\n       LOOP\n         FETCH emp_cursor INTO v_employee_name;\n         EXIT WHEN emp_cursor%NOTFOUND;\n         DBMS_OUTPUT.PUT_LINE(v_employee_name);\n       END LOOP;\n       CLOSE emp_cursor;\n     END;