INSERT INTO SpeciesFoundInCountry (CountryID, SpeciesID)\n   SELECT\n       c.CountryID,\n       s.SpeciesID\n   FROM\n       original_table o\n   JOIN\n       Species s ON o.Species = s.SpeciesName\n   JOIN\n       Country c ON (\n           (c.CountryName = 'Afghanistan' AND o.Afghanistan = 1) OR\n           (c.CountryName = 'Albania' AND o.Albania = 1) OR\n           (c.CountryName = 'Algeria' AND o.Algeria = 1) OR\n           -- Add all other country conditions here\n           (c.CountryName = 'Western Sahara' AND o.`Western Sahara` = 1) OR\n           (c.CountryName = 'Yemen' AND o.Yemen = 1) OR\n           (c.CountryName = 'Zambia' AND o.Zambia = 1) OR\n           (c.CountryName = 'Zimbabwe' AND o.Zimbabwe = 1)\n       );