-- Create a temporary table to hold intermediate results\nCREATE TABLE #TempResults\n(\n    COL1 INT,\n    COL2 FLOAT,\n    COL3 VARCHAR(255),\n    -- Add other columns as necessary\n);\n\n-- Insert data into the temporary table with necessary transformations\nINSERT INTO #TempResults (COL1, COL2, COL3, ...)\nSELECT \n    DATEPART(YEAR, t1.SomeDateColumn) AS COL1, -- Adjust date part as needed\n    t2.SomeValue / 1.1 AS COL2,                 -- Float operation\n    t3.SomeString + ' ' + t1.AnotherString AS COL3, -- String concatenation\n    ...\nFROM TABLE_1 t1\nINNER JOIN TABLE_2 t2 ON t1.key = t2.foreignKey\nINNER JOIN TABLE_3 t3 ON t2.key = t3.foreignKey;\n\n-- Insert the processed data into the final table\nINSERT INTO MyTable (COL1, COL2, COL3, ...)\nSELECT COL1, COL2, COL3, ...\nFROM #TempResults;\n\n-- Drop the temporary table\nDROP TABLE #TempResults;