-- Assuming you have a table named 'YourTable' with a datetime column 'YourDateColumn'\n-- and you want to update the month of 'YourDateColumn' to a given int value @NewMonth\n\nDECLARE @NewMonth INT = 5; -- Example new month value\nDECLARE @Year INT;\nDECLARE @Day INT;\n\n-- Update the datetime column with the new month\nUPDATE YourTable\nSET YourDateColumn = DATEADD(MONTH, @NewMonth - MONTH(YourDateColumn), YourDateColumn)\nWHERE YourDateColumn IS NOT NULL;