CREATE TABLE key_value_store (\n    record_id INT,\n    key_name VARCHAR(255),\n    key_value VARCHAR(255),\n    PRIMARY KEY (record_id, key_name)\n);\n\n-- Adding a key-value pair\nINSERT INTO key_value_store (record_id, key_name, key_value) VALUES (1, 'key1', 'value1');\n\n-- Removing a key-value pair\nDELETE FROM key_value_store WHERE record_id = 1 AND key_name = 'key1';\n\n-- Querying specific key-value pairs\nSELECT key_name, key_value FROM key_value_store WHERE record_id = 1;