CREATE TABLE record_store (\n    record_id INT PRIMARY KEY,\n    key1 VARCHAR(255),\n    key2 VARCHAR(255),\n    -- Add more columns as needed\n);\n\n-- Adding a key-value pair requires altering the table if the column does not exist\nALTER TABLE record_store ADD COLUMN key3 VARCHAR(255);\n\n-- Updating a key-value pair\nUPDATE record_store SET key1 = 'value1' WHERE record_id = 1;\n\n-- Querying all key-value pairs\nSELECT * FROM record_store WHERE record_id = 1;