DECLARE @RoleName NVARCHAR(128) = 'YourRoleName'; -- Replace with your role name\nDECLARE @SQL NVARCHAR(MAX) = '';\n\n-- Generate the SQL script to grant permissions\nSELECT @SQL = @SQL + 'GRANT SELECT, REFERENCES, INSERT, UPDATE, DELETE ON [' + SCHEMA_NAME(schema_id) + '].[' + name + '] TO [' + @RoleName + '];' + CHAR(13)\nFROM sys.tables\nWHERE is_ms_shipped = 0; -- Exclude system tables\n\n-- Execute the generated SQL script\nEXEC sp_executesql @SQL;