CREATE TRIGGER payment_insert_trigger\n    AFTER INSERT ON PAYMENT\n    FOR EACH ROW\n    EXECUTE FUNCTION log_timestamp('PAYMENT', 1);\n\n    CREATE TRIGGER payment_update_trigger\n    AFTER UPDATE ON PAYMENT\n    FOR EACH ROW\n    EXECUTE FUNCTION log_timestamp('PAYMENT', 2);\n\n    CREATE TRIGGER payment_delete_trigger\n    AFTER DELETE ON PAYMENT\n    FOR EACH ROW\n    EXECUTE FUNCTION log_timestamp('PAYMENT', 3);