-- SQL Server example\nDECLARE @viewName NVARCHAR(128) = 'MODEL_SYSTEM_1';\nDECLARE @sql NVARCHAR(MAX) = 'CREATE VIEW ' + @viewName + ' AS SELECT ';\n\n-- Fetch columns from fact and dimension tables\nSELECT @sql = @sql + STRING_AGG(\n    '[' + TABLE_NAME + '].[' + COLUMN_NAME + '] AS [' + TABLE_NAME + '_' + COLUMN_NAME + ']', \n    ', '\n)\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE TABLE_NAME IN ('fact', 'conformed_dim', 'nonconformed_dim', 'date_dim');\n\n-- Join tables\nSET @sql = @sql + ' FROM fact ' +\n            'INNER JOIN conformed_dim ON conformed_dim.cdim_dim_id = fact.cdim_dim_id ' +\n            'INNER JOIN nonconformed_dim ON nonconformed_dim.ncdim_dim_id = fact.ncdim_dim_id ' +\n            'INNER JOIN date_dim ON date_dim.date_id = fact.date_id;';\n\n-- Execute the dynamic SQL to create the view\nEXEC sp_executesql @sql;