-- Find the SID and Serial# of the session running the report\n   SELECT sid, serial# FROM v$session WHERE username = 'YOUR_USERNAME';\n\n   -- Start tracing for that session\n   EXEC DBMS_MONITOR.SESSION_TRACE_ENABLE(session_id => :SID, serial_num => :SERIAL);\n\n   -- Run the report generation job here\n\n   -- Stop tracing\n   EXEC DBMS_MONITOR.SESSION_TRACE_DISABLE(session_id => :SID, serial_num => :SERIAL);