-- Create a table\nCREATE TABLE employees (\n    id INT PRIMARY KEY,\n    name VARCHAR(100),\n    department_id INT\n);\n\n-- Create an updatable view\nCREATE VIEW v_employees AS\nSELECT id, name, department_id\nFROM employees\nWHERE department_id IS NOT NULL\nWITH CHECK OPTION;\n\n-- Insert data into the view\nINSERT INTO v_employees (id, name, department_id)\nVALUES (1, 'John Doe', 2);