-- Create Partition Function\nCREATE PARTITION FUNCTION PartitionMonthlyCurrentYear (DATETIME)\nAS RANGE LEFT FOR VALUES (\n    '2023-01-31 23:59:59.997',\n    '2023-02-28 23:59:59.997',\n    '2023-03-31 23:59:59.997',\n    '2023-04-30 23:59:59.997',\n    '2023-05-31 23:59:59.997',\n    '2023-06-30 23:59:59.997',\n    '2023-07-31 23:59:59.997',\n    '2023-08-31 23:59:59.997',\n    '2023-09-30 23:59:59.997',\n    '2023-10-31 23:59:59.997',\n    '2023-11-30 23:59:59.997',\n    '2023-12-31 23:59:59.997'\n);\n\n-- Create Partition Scheme\nCREATE PARTITION SCHEME MonthlyPartitionScheme\nAS PARTITION PartitionMonthlyCurrentYear\nTO (FileGroup1, FileGroup2, FileGroup3, FileGroup4, FileGroup5, FileGroup6, FileGroup7, FileGroup8, FileGroup9, FileGroup10, FileGroup11, FileGroup12);\n\n-- Create Partitioned Table\nCREATE TABLE PerformanceData (\n    ID INT IDENTITY(1,1) PRIMARY KEY,\n    ItemID INT,\n    ClickCount INT,\n    ClickDate DATETIME\n)\nON MonthlyPartitionScheme (ClickDate);\n\n-- Create Index on Partitioned Table\nCREATE CLUSTERED INDEX IDX_PerformanceData_ClickDate\nON PerformanceData (ClickDate)\nON MonthlyPartitionScheme (ClickDate);