-- Update statistics on the production system\nUPDATE STATISTICS [YourTableName];\n\n-- Rebuild indexes if necessary\nALTER INDEX ALL ON [YourTableName] REBUILD;\n\n-- Optionally clear the plan cache (use with caution)\nDBCC FREEPROCCACHE;\n\n-- Add the RECOMPILE option to the stored procedure (if parameter sniffing is suspected)\nEXEC sp_executesql N'YourStoredProcedure', N'@Param1 datatype', @Param1 OPTION (RECOMPILE);\n\n-- Check server configuration for differences\nEXEC sp_configure;